import { createPortal } from "react-dom";

export default function Modal({children, onClose}){
    function handleClose(){
        onClose();
    }
    
    return <>
    {createPortal(
        <div className="modal">
            {children}
        </div>,
        document.getElementById("modal")
    )}
    {
        createPortal(
            <div className="backdrop"  onClick={handleClose}/>,
            document.getElementById("modal")
        )
    }
    </>
}
