import { createPortal } from "react-dom";

export default function Modal({children, onClose}){
    

    
    return <>
    {createPortal(
        <div className="modal">
            {children}
        </div>,
        document.getElementById("modal")
    )}
    {
        createPortal(
            <div className="backdrop"  onClose={onClose}/>,
            document.getElementById("modal")
        )
    }
    </>
}
